class Solution
{
  public:
    bool isSubsequence(string s, string t)
    {
        int m = s.size();
        if (m == 0)
            return true;
        int n = t.size();
        if (n == 0)
            return false;

        vector<int> res(n + 1, 10);

        for (int i = 1; i <= m; i++)
        {
            res[0] = 1;

            for (int j = 1; j <= n; j++)
            {

                if (s[i - 1] == t[j - 1])
                {
                    int old = res[j] / 10;
                    int now = res[j - 1] % 10;
                    res[j] = now * 10 + old;
                    
                }
                else
                {
                    int now = res[j - 1] / 10;
                    int old = res[j] / 10;
                    res[j] = now * 10 + old;
                }
            }
        }

        return res[n] / 10;
    }
};

int main()
{
    Solution a;
    string s, t;
    s = "leeeeetcode";
    t = "";
    cout << a.isSubsequence(s, t) << endl;
    return 0;
}
